import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { useCart } from "./CartContext";
import API_BASE_URL from "./config";
import Slider from "react-slick";
import './ViewProduct.css';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { FaStar, FaStarHalfAlt, FaRegStar, FaChevronLeft, FaChevronRight, FaInfoCircle } from "react-icons/fa";
import Header from "./header/Header";
import Footer from "./footer/footer";
import Recommendation from "./Recommendation";
import Swal from 'sweetalert2';  // SweetAlert2 for modal

const CACHE_KEY_PREFIX = "amazon_clone_";

const ViewProduct = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const parentAsin = queryParams.get("asin");
  const { addToCart, cart } = useCart();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All Categories");
  const [showUserPanel, setShowUserPanel] = useState(false);
  const [userId, setUserId] = useState("1234"); // Default userId

  const [product, setProduct] = useState(null);
  const [recommendations, setRecommendations] = useState({ data: [], loading: true });
  const [userRecommendations, setUserRecommendations] = useState({ data: [], loading: true });
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState(null);

  useEffect(() => {
    if (!parentAsin) {
      setErrorMessage("Invalid Product ID.");
      setLoading(false);
      return;
    }

    setLoading(true);
    setErrorMessage(null);
    setRecommendations({ data: [], loading: true });
    setUserRecommendations({ data: [], loading: true });

    // Fetch product details
    axios.get(`${API_BASE_URL}/products/product/${parentAsin}`, { timeout: 8000 })
      .then((response) => {
        if (!response.data || !response.data.data || response.data.data.message === "Product not found") {
          throw new Error("Product not found.");
        }
        
        const productData = response.data.data;
        setProduct(productData);
        setLoading(false);

        return productData;
      })
      .then((productData) => {
        // Fetch brand/category based recommendations
        if (productData.brand?.brandName && productData.category?.categoryName) {
          return axios.get(`${API_BASE_URL}/brands/brandOnly`, {
            params: {
              brandName: encodeURIComponent(productData.brand.brandName.trim()),
              categoryName: encodeURIComponent(productData.category.categoryName.trim()),
              currentProductId: encodeURIComponent(parentAsin)
            },
            timeout: 10000
          })
          .then((brandResponse) => {
            let recommendedProducts = [];
            
            if (brandResponse.data && Array.isArray(brandResponse.data.data)) {
              recommendedProducts = brandResponse.data.data;
            }

            setRecommendations({
              data: recommendedProducts,
              loading: false
            });

            return productData;
          });
        }

        setRecommendations({ data: [], loading: false });
        return productData;
      })
      .then((productData) => {
        // Fetch user-based recommendations
        return axios.get(`${API_BASE_URL}/users/recommendations`, {
          params: {
            userId: encodeURIComponent(userId),
            currentProductId: encodeURIComponent(parentAsin)
          },
          timeout: 10000
        })
        .then((userResponse) => {
          let userRecommendedProducts = [];
          
          if (userResponse.data && Array.isArray(userResponse.data.data)) {
            userRecommendedProducts = userResponse.data.data.map(item => ({
              parentAsin: item.productId,
              title: item.title,
              description: item.description,
              price: item.price,
              averageRating: item.averageRating,
              ratingNumber: item.ratingNumber,
              mainImage: item.mainImage,
              allImages: item.allImages || [],
              sharedUserCount: item.sharedUserCount
            }));
          }

          setUserRecommendations({
            data: userRecommendedProducts,
            loading: false
          });

          return productData;
        });
      })
      .catch((error) => {
        setErrorMessage(error.message || "Failed to fetch product details.");
        setLoading(false);
        setRecommendations({ data: [], loading: false });
        setUserRecommendations({ data: [], loading: false });
      });
  }, [parentAsin, userId]);

  // Function to show user-based recommendation info
  const showUserBasedInfo = () => {
    Swal.fire({
      title: 'User-Based Recommendations',
      html: `
        <p>These recommendations are based on users with similar interests.</p>
        <p>We analyze users who interacted with the same products as you and recommend items they liked.</p>
        <p>The more users who have similar tastes recommended a product, the higher it appears in results.</p>
      `,
      icon: 'info',
      confirmButtonText: 'Got it'
    });
  };

  return (
    <div>
      <Header 
        onSearch={handleSearch} 
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        onCategoryChange={handleCategoryChange}
        selectedCategory={selectedCategory}
        cartLength={cart?.length || 0}
        onUserPanelToggle={handleUserPanelToggle}
        showUserPanel={showUserPanel}
        handleUserIdSubmit={handleUserIdSubmit}
        userId={userId}
        setUserId={setUserId}
        error={null}
      />
      <div className="container my-5" style={{ paddingTop: "50px", marginBottom: "50px" }}>
        {loading ? <div>Loading product details...</div> : null}
        
        {/* Display Product Details */}
        <h1>{product?.title}</h1>
        <h3>Price: ${product?.price}</h3>
        <p>Category: {product?.category?.categoryName}</p>
        <p>Brand: {product?.brand?.brandName}</p>

        {/* Display Recommendations */}
        {recommendations.data.length > 0 && (
          <div className="recomm mt-5">
            <h2 style={{ display: 'flex', alignItems: 'center' }}>
              Similar Products
              <div
                title="How recommendations work"
                style={{
                  width: '25px',
                  height: '25px',
                  borderRadius: '50%',
                  backgroundColor: '#219EBC',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  fontSize: '16px',
                  marginLeft: '10px'
                }}
                onClick={() => showBrandCategoryInfo(recommendations)}
              >
                i
              </div>
            </h2>
            <Recommendation recommendations={recommendations} onPrefetch={prefetchProductData} />
          </div>
        )}

        {/* User-Based Recommendations */}
        {userRecommendations.data.length > 0 && (
          <div className="recomm mt-5">
            <h2 style={{ display: 'flex', alignItems: 'center' }}>
              Recommended For You
              <div
                title="How user recommendations work"
                style={{
                  width: '25px',
                  height: '25px',
                  borderRadius: '50%',
                  backgroundColor: '#219EBC',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  fontSize: '16px',
                  marginLeft: '10px'
                }}
                onClick={showUserBasedInfo}
              >
                i
              </div>
            </h2>
            <Recommendation recommendations={userRecommendations} onPrefetch={prefetchProductData} />
          </div>
        )}

      </div>
      <Footer />
    </div>
  );
};

export default ViewProduct;
