import React, { useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import { LazyLoadImage } from 'react-lazy-load-image-component';
import Slider from "react-slick"; 
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import { FaChevronLeft, FaChevronRight, FaStar, FaStarHalfAlt, FaRegStar, FaUsers } from 'react-icons/fa';
import { showBrandCategoryInfo } from './BrandCategoryRecommendation';
import Swal from 'sweetalert2';

const styles = {
  container: {
    position: 'relative',
    marginTop: '10px',
    marginBottom: '20px'
  },
  paginationContainer: {
    display: 'flex',
    justifyContent: 'flex-end',
    alignItems: 'center', 
    marginBottom: '10px'
  },
  productCard: {
    border: '1px solid #e0e0e0',
    borderRadius: '8px',
    padding: '15px',
    margin: '10px',
    textAlign: 'center',
    transition: 'box-shadow 0.3s ease',
    cursor: 'pointer',
    height: '400px',
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  cardImgContainer: {
    height: '250px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: '15px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    overflow: 'hidden',
  },
  productImage: {
    width: '100%',
    height: '100%',
    objectFit: 'cover', 
    transition: 'transform 0.3s ease',
  },
  productTitle: {
    fontSize: '12px',
    fontWeight: '500',
    marginBottom: '5px',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    color: '#333',
  },
  productPrice: {
    fontSize: '14px',
    fontWeight: 'bold',
    color: '#040697',
    marginBottom: '5px',
  },
  starRating: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 'auto',
  },
  addToCartBtn: {
    backgroundColor: '#ffe600',
    border: 'none',
    borderRadius: '20px',
    padding: '10px',
    fontWeight: '500',
    marginTop: 'auto',
    transition: 'background-color 0.3s',
  },
  sharedUsersBadge: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '11px',
    backgroundColor: '#e3f2fd',
    color: '#1565c0',
    borderRadius: '12px',
    padding: '2px 8px',
    marginTop: '5px',
    marginBottom: '5px',
  }
};

// Custom arrow components
const PrevArrow = (props) => {
  const { className, style, onClick } = props;
  return (
    <div 
      className={className}
      style={{ 
        ...style, 
        display: "block", 
        background: "rgba(0,0,0,0.2)",
        borderRadius: "50%",
        padding: "6px 9px",
        zIndex: 10,
        color: "black",
        fontSize: "20px",
        width: "40px",
        height: "43px",
        left: "-25px",
        cursor: "pointer"
      }}
      onClick={onClick}
    >
      <FaChevronLeft />
    </div>
  );
};

const NextArrow = (props) => {
  const { className, style, onClick } = props;
  return (
    <div 
      className={className}
      style={{ 
        ...style, 
        display: "block", 
        background: "rgba(0,0,0,0.2)",
        borderRadius: "50%",
        padding: "6px 9px",
        zIndex: 10,
        color: "black",
        fontSize: "20px",
        width: "40px",
        height: "43px",
        right: "-28px",
        cursor: "pointer"
      }}
      onClick={onClick}
    >
      <FaChevronRight />
    </div>
  );
};

const renderStars = (rating, ratingCount) => {
  if (!rating) return null;

  const fullStars = Math.floor(rating);
  const remainder = rating - fullStars;
  const maxStars = 5;
  let stars = [];

  for (let i = 0; i < fullStars; i++) {
    stars.push(<FaStar key={i} color="#FFC107" />);
  }

  if (remainder >= 0.5) {
    stars.push(<FaStarHalfAlt key="half" color="#FFC107" />);
  }

  while (stars.length < maxStars) {
    stars.push(<FaRegStar key={stars.length} color="#FFC107" />);
  }

  return (
    <div style={styles.starRating}>
      {stars}
      {ratingCount > 0 && <span style={{ marginLeft: '5px', fontSize: '12px' }}>({ratingCount})</span>}
    </div>
  );
};

// Function to show user-based recommendation info
const showUserBasedInfo = () => {
  Swal.fire({
    title: 'User-Based Recommendations',
    html: `
      <p>These recommendations are based on users with similar interests.</p>
      <p>We analyze users who interacted with the same products as you and recommend items they liked.</p>
      <p>The more users who have similar tastes recommended a product, the higher it appears in results.</p>
    `,
    icon: 'info',
    confirmButtonText: 'Got it'
  });
};

const ProductCard = ({ product, navigate, onPrefetch, isUserBased }) => {
  if (!product) return null;

  const handleClick = () => {
    if (product.parentAsin) {
      navigate(`/viewProduct?asin=${product.parentAsin}`);
    }
  };

  const handleMouseOver = () => {
    if (onPrefetch && typeof onPrefetch === 'function') {
      onPrefetch(product.parentAsin);
    }
  };

  return (
    <div
      style={styles.productCard}
      onClick={handleClick}
      onMouseOver={handleMouseOver}
      onMouseEnter={(e) => e.currentTarget.style.boxShadow = '0 4px 6px rgba(0, 0, 0, 0.1)'}
      onMouseLeave={(e) => e.currentTarget.style.boxShadow = 'none'}
    >
      {/* Product Image */}
      <div style={styles.cardImgContainer}>
        <LazyLoadImage
          src={product.mainImage || product.image || 'https://via.placeholder.com/200?text=No+Image'}
          alt={product.title}
          style={styles.productImage}
        />
      </div>

      {/* Product Title */}
      <div style={styles.productTitle}>{product.title}</div>

      {/* Product Price */}
      <div style={styles.productPrice}>${product.price}</div>

      {/* Star Rating */}
      {renderStars(product.averageRating, product.ratingNumber)}
      
      {/* Display shared users count for user recommendations */}
      {isUserBased && product.sharedUserCount > 0 && (
        <div style={styles.sharedUsersBadge}>
          <FaUsers style={{ marginRight: '5px' }} />
          {product.sharedUserCount} similar {product.sharedUserCount === 1 ? 'user' : 'users'} like this
        </div>
      )}

      {/* Add to Cart Button */}
      <button
        style={styles.addToCartBtn}
        onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#FFC300'}
        onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#FFD814'}
      >
        Add to Cart
      </button>
    </div>
  );
};

const Recommendation = ({ recommendations, onPrefetch, isUserBased = false }) => {
  const navigate = useNavigate();
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil((recommendations?.data?.length || 0) / 5);

  const sortedRecommendations = useMemo(() => {
    if (!recommendations?.data) return [];

    if (isUserBased) {
      // Sort user recommendations by shared user count if available
      return [...recommendations.data].sort((a, b) => 
        (b.sharedUserCount || 0) - (a.sharedUserCount || 0)
      );
    } else {
      // Prioritize brand/category recommendations
      const priorityMap = {
        1: [], // Same brand, same category
        2: [], // Same brand, different category
        3: [], // Different brand, same category
        4: []  // Random products
      };

      recommendations.data
        .filter(product => product && product.parentAsin && (product.image || product.mainImage) && product.title && product.price)
        .forEach(product => {
          const priority = product.priority || 4;
          priorityMap[priority].push(product);
        });

      return [
        ...priorityMap[1],
        ...priorityMap[2],
        ...priorityMap[3],
        ...priorityMap[4]
      ];
    }
  }, [recommendations, isUserBased]);

  const sliderSettings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5,
    afterChange: (current) => setCurrentPage(Math.floor(current / 5) + 1),
    prevArrow: <PrevArrow />,
    nextArrow: <NextArrow />,
    responsive: [
      {
        breakpoint: 1200,
        settings: { slidesToShow: 4, slidesToScroll: 4 }
      },
      {
        breakpoint: 1024,
        settings: { slidesToShow: 3, slidesToScroll: 3 }
      },
      {
        breakpoint: 600,
        settings: { slidesToShow: 2, slidesToScroll: 2 }
      },
      {
        breakpoint: 480,
        settings: { slidesToShow: 1, slidesToScroll: 1 }
      }
    ]
  };

  return (
    <div style={styles.container}>
      <div style={styles.paginationContainer}>
        <span>Page {currentPage} of {totalPages || 1}</span>
        <div
          title={isUserBased ? "How user recommendations work" : "How recommendations work"}
          style={{
            width: '25px',
            height: '25px',
            borderRadius: '50%',
            backgroundColor: '#219EBC',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            fontSize: '16px',
            marginLeft: '10px'
          }}
          onClick={() => isUserBased ? showUserBasedInfo() : showBrandCategoryInfo(recommendations)}
        >
          i
        </div>
      </div>
      
      {recommendations?.loading ? (
        <div>Loading recommendations...</div>
      ) : sortedRecommendations.length === 0 ? (
        <div>No recommendations found</div>
      ) : (
        <Slider {...sliderSettings}>
          {sortedRecommendations.map((product, index) => (
            <ProductCard
              key={`${product.parentAsin}-${index}`}
              product={product}
              navigate={navigate}
              onPrefetch={onPrefetch}
              isUserBased={isUserBased}
            />
          ))}
        </Slider>
      )}
    </div>
  );
};

export default Recommendation;
