import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "./navbar/Navbar";
import Footer from "./footer/footer";
import API_BASE_URL from "./config";
import Slider from "react-slider";

const CACHE_KEY_PREFIX = "amazon_clone_";

const SearchResult = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const searchKeyword = queryParams.get("keyword") || "";
  const priceRangeFromUrl = queryParams.get("priceRange") ? JSON.parse(queryParams.get("priceRange")) : [0, 1000];

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [priceRange, setPriceRange] = useState(priceRangeFromUrl);
  const [hasSearched, setHasSearched] = useState(false);
  const [sortOption, setSortOption] = useState("lowToHigh");

  const [searchTerm, setSearchTerm] = useState(searchKeyword);
  const [currentSearchTerm, setCurrentSearchTerm] = useState(searchKeyword);

  // Simplified cache management functions
  const getCacheKey = (keyword, priceRange) => {
    return `${CACHE_KEY_PREFIX}search_${keyword}_${JSON.stringify(priceRange)}`;
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const saveToCache = (key, data) => {
    localStorage.setItem(key, JSON.stringify({
      timestamp: Date.now(),
      data: data
    }));
  };

  const getFromCache = (key) => {
    const cached = localStorage.getItem(key);
    if (!cached) return null;
    
    const parsedCache = JSON.parse(cached);
    // Cache valid for 30 minutes
    if (Date.now() - parsedCache.timestamp > 30 * 60 * 1000) {
      localStorage.removeItem(key);
      return null;
    }
    
    return parsedCache.data;
  };

  // Function to fetch products from the API
  const fetchProducts = (searchTermToUse) => {
    if (!searchTermToUse || !searchTermToUse.trim()) {
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

    // Try to get from cache first
    const cacheKey = getCacheKey(searchTermToUse, priceRange);
    const cachedProducts = getFromCache(cacheKey);
    
    if (cachedProducts) {
      setProducts(cachedProducts);
      setLoading(false);
      setHasSearched(true);
      return;
    }

    axios
      .get(`${API_BASE_URL}/products/search`, {
        params: { keyword: searchTermToUse, priceRange: JSON.stringify(priceRange) },
        timeout: 10000,
      })
      .then((response) => {
        if (response.data && response.data.data) {
          let filteredProducts = response.data.data.filter((item) => {
            // Split search term into words to check if any word matches
            const searchTerms = searchTermToUse.toLowerCase().split(' ');
            const hasAnySearchTerm = searchTerms.some(term => {
              const titleMatch = item.product.title && item.product.title.toLowerCase().includes(term);
              const descMatch = item.product.description && item.product.description.toLowerCase().includes(term);
              const brandMatch = item.brand?.brandName && item.brand.brandName.toLowerCase().includes(term);
              const categoryMatch = item.category?.categoryName && item.category.categoryName.toLowerCase().includes(term);
              
              return titleMatch || descMatch || brandMatch || categoryMatch;
            });

            const hasImage = item.mainImage && item.mainImage !== "https://via.placeholder.com/200";
            return (
              hasAnySearchTerm &&
              hasImage &&
              item.product.price >= priceRange[0] &&
              item.product.price <= priceRange[1]
            );
          });

          setProducts(filteredProducts);
          saveToCache(cacheKey, filteredProducts);
        } else {
          setProducts([]);
        }
        setLoading(false);
        setHasSearched(true);
      })
      .catch((error) => {
        console.error("Error fetching search results:", error);
        setError("Failed to load search results. Please try again.");
        setProducts([]);
        setLoading(false);
        setHasSearched(true);
      });
  };

  // Initial load
  useEffect(() => {
    if (searchKeyword) {
      setCurrentSearchTerm(searchKeyword);
      fetchProducts(searchKeyword);
    } else {
      setLoading(false);
    }
  }, []);

  // Price range change effect
  useEffect(() => {
    if (currentSearchTerm) {
      fetchProducts(currentSearchTerm);
    }
  }, [priceRange]);

  // Sorting logic
  const sortedProducts = [...products].sort((a, b) => {
    if (sortOption === "lowToHigh") return a.product.price - b.product.price;
    if (sortOption === "highToLow") return b.product.price - a.product.price;
    if (sortOption === "ratingHighToLow") return b.product.averageRating - a.product.averageRating;
    if (sortOption === "ratingLowToHigh") return a.product.averageRating - b.product.averageRating;
    return 0;
  });

  // Handle search function for Navbar - only executes on button click
  const handleSearch = (newSearchTerm) => {
    if (newSearchTerm && newSearchTerm.trim() !== "") {
      setSearchTerm(newSearchTerm);
      setCurrentSearchTerm(newSearchTerm);
      navigate(`/search?keyword=${encodeURIComponent(newSearchTerm)}&priceRange=${JSON.stringify(priceRange)}`);
      fetchProducts(newSearchTerm);
    }
  };

  // Handle search term change without performing search
  const handleSearchTermChange = (term) => {
    setSearchTerm(term);
  };

  // Handle view product click
  const handleViewProduct = (productId) => {
    if (productId) {
      navigate(`/viewProduct?asin=${encodeURIComponent(productId)}&keyword=${encodeURIComponent(currentSearchTerm)}&priceRange=${JSON.stringify(priceRange)}`);
    }
  };

  return (
    <div>
      <Navbar 
        onSearch={handleSearch}
        onSearchTermChange={handleSearchTermChange} 
        initialSearchValue={searchTerm} 
      />
      <div className="container my-5">
        <h2>Search Results for: {currentSearchTerm}</h2>

        <div className="sort-container">
          <label htmlFor="sort">Sort by: </label>
          <select id="sort" value={sortOption} onChange={handleSortChange}>
            <option value="lowToHigh">Price: Low to High</option>
            <option value="highToLow">Price: High to Low</option>
            <option value="ratingHighToLow">Rating: High to Low</option>
            <option value="ratingLowToHigh">Rating: Low to High</option>
          </select>
        </div>

        {loading ? (
          <div className="text-center">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mt-2">Loading search results...</p>
          </div>
        ) : error ? (
          <div className="alert alert-danger">
            {error}
          </div>
        ) : (
          <div className="row">
            {products.length === 0 ? (
              <div className="col-12">
                <div className="alert alert-info">
                  {hasSearched ? (
                    <div>
                      No products found for "{currentSearchTerm}". Try a different search term.
                    </div>
                  ) : (
                    <div>
                      Failed to load products. Please try again.
                    </div>
                  )}
                </div>
              </div>
            ) : (
              sortedProducts.map((productItem) => {
                const { product, category, store, mainImage } = productItem;

                // Skip rendering if product is missing or doesn't have a valid image
                if (!product || !product.parentAsin || !mainImage) return null;

                return (
                  <div key={product.parentAsin} className="col-12 col-sm-6 col-md-4 col-lg-3 my-3">
                    <div
                      className="card h-100"
                      onClick={() => handleViewProduct(product.parentAsin)}
                      style={{ cursor: "pointer" }}
                    >
                      <img
                        className="card-img-top"
                        src={mainImage || "https://via.placeholder.com/200"}
                        alt={product.title || "Product"}
                        style={{ height: "200px", objectFit: "contain", padding: "10px" }}
                      />
                      <div className="card-body d-flex flex-column">
                        <h5 className="card-title">{product.title || "Unknown Product"}</h5>
                        <p className="text-muted">{category?.categoryName || "Uncategorized"}</p>
                        <div className="d-flex justify-content-between">
                          {product.averageRating && <span>‚≠ê {product.averageRating.toFixed(1)}</span>}
                        </div>
                        <div className="mt-auto d-flex justify-content-between align-items-center">
                          <h5 className="m-0">
                            {product.price ? `$${product.price.toFixed(2)}` : "N/A"}
                          </h5>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default SearchResult;
