import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import axios from 'axios';
import { FaStar, FaStarHalfAlt, FaRegStar,FaChevronLeft, FaChevronRight,FaInfoCircle } from 'react-icons/fa';
import Slider from 'react-slick';
import './Info.css'; // Import the CSS for styling

// Slider settings
const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    lazyLoad: 'ondemand',
    nextArrow: <FaChevronRight />,
    prevArrow: <FaChevronLeft />,
  };
  

const BrandCategoryRecommendation = ({ brandName, categoryName, currentProductId, triggerButton }) => {
  const [topRecommendations, setTopRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [scoringDetails, setScoringDetails] = useState({
    ratingCountRanges: [],
    calculationDetails: []
  });
  const [error, setError] = useState(null);

  useEffect(() => {
    if (brandName && categoryName) {
      fetchTopRecommendations(brandName, categoryName, currentProductId);
    }
  }, [brandName, categoryName, currentProductId]);

  const formatNumber = (num) => {
    return num.toLocaleString('en-US', { maximumFractionDigits: 0 });
  };

  // Fetch recommendations from API
  const fetchTopRecommendations = async (brandName, categoryName, currentProductId) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:9005/api/brands/brandOnly`, {
        params: {
          brandName,
          categoryName,
          currentProductId
        }
      });
      const data = response.data;
      const processedData = calculateScores(data);
      setTopRecommendations(processedData.topFive);
      setScoringDetails({
        ratingCountRanges: processedData.ratingCountRanges,
        calculationDetails: processedData.calculationDetails
      });
    } catch (err) {
      setError(err.message);
      setTopRecommendations([]);
      setScoringDetails({ ratingCountRanges: [], calculationDetails: [] });
    } finally {
      setLoading(false);
    }
  };

  // Calculate scores based on brand and category priority
  const calculateScores = (products) => {
    if (!products || products.length === 0) return { topFive: [], ratingCountRanges: [], calculationDetails: [] };

    const sanitized = products.map((p) => ({
      ...p,
      productId: p.productId || `product-${Math.random().toString(36).substr(2, 9)}`,
      productName: p.productName || p.title || 'Unknown Product',
      brand: p.brand || p.brandName || 'Unknown Brand',
      rating: p.rating || p.averageRating || 0,
      ratingCount: p.ratingCount || p.ratingNumber || p.userCount || 0,
      price: p.price || 0,
      category: p.category || 'Unknown Category',
    }));

    const calculationDetails = sanitized.map((product) => {
      let brandScore = product.brand === brandName ? 5 : 3; // 5 for same brand, 3 for different brand
      let categoryScore = product.category === categoryName ? 5 : 4; // 5 for same category, 4 for different category

      const totalScore = brandScore + categoryScore;

      return {
        ...product,
        brandScore,
        categoryScore,
        totalScore,
      };
    }).sort((a, b) => b.totalScore - a.totalScore || b.ratingCount - a.ratingCount);

    calculationDetails.forEach((p, i) => (p.rank = i + 1));

    return {
      topFive: calculationDetails.slice(0, 5),
      ratingCountRanges: [],
      calculationDetails,
    };
  };

  // Function to show modal with detailed information
  const showModal = () => {
    Swal.fire({
      title: 'Brand and Category Recommendation Info',
      html: `
        <div style="text-align: left; max-height: 70vh; overflow-y: auto;">
          <h3>How Recommendations Work</h3>
          <p>We analyze the brand and category of a product you are viewing to suggest similar items.</p>
          <ul>
            <li><strong>Priority 1:</strong> Same Brand, Same Category (10 points total)</li>
            <li><strong>Priority 2:</strong> Same Category, Different Brand (8 points total)</li>
            <li><strong>Priority 3:</strong> Same Brand, Different Category (9 points total)</li>
            <li><strong>Priority 4:</strong> Different Brand, Different Category (7 points total)</li>
          </ul>
          <h3>Scoring Pattern</h3>
          <p>Recommendations are ranked based on brand and category priority:</p>
          <ul>
            <li><strong>Same Brand:</strong> 5 points</li>
            <li><strong>Same Category:</strong> 5 points</li>
            <li><strong>Different Brand:</strong> 3 points</li>
            <li><strong>Different Category:</strong> 4 points</li>
          </ul>
        </div>
      `,
      width: 800,
      confirmButtonText: 'Got it!',
      confirmButtonColor: '#046e8f',
    });
  };
  
  return (
    <div>
      <Slider {...sliderSettings}>
        {topRecommendations.map((product, index) => (
          <div key={index}>
            <div className="product-card" onClick={() => showModal()}>
              <LazyLoadImage
                src={product.image}
                alt={product.productName}
                className="img-fluid"
              />
              <h5>{product.productName}</h5>
              <p>{product.price}</p>
            </div>
          </div>
        ))}
      </Slider>
      <FaInfoCircle
        style={{ marginLeft: '10px', cursor: 'pointer' }}
        onClick={showModal}
        title="How recommendations work"
      />
    </div>
  );
};

export default BrandCategoryRecommendation;
